using System.Collections.Generic;using System.Linq;using PalRSA.Core.DataAccess;using PalRSA.Core.DataAccess;using System.Data.Entity;namespace Recapture.DataAccess{    public class RelationshipDb    {        private readonly PALSiteDBEntities _biometricEntities = InternetConnection.CheckForInternetConnection();        public List<Relationship> GetRelationshipList()        {            //return _biometricEntities.States.Where(m => m.Active).ToList();            return _biometricEntities.Relationships.Where(m => m.Active == true).OrderBy(x => x.Name).ToList();        }        public void AddRelationship(Relationship relationship)        {            _biometricEntities.Relationships.Add(relationship);            _biometricEntities.SaveChanges();        }        public void UpdateRelationship(Relationship relationship)        {            _biometricEntities.Entry(relationship).State = EntityState.Modified;            _biometricEntities.SaveChanges();        }     }}